CREATE VIEW V_FOOD_LANGUAL AS SELECT l.FACTOR, DESCRIPTION FROM FOOD_DES f RIGHT JOIN LANGUAL l ON l.NDB_NO = f.NDB_NO LEFT JOIN LANGDESC ld on ld.factor = l.factor where f.ndb_no = "43365";

CREATE VIEW V_FOOD_NUTRIENT AS  SELECT fd.NDB_NO, ndef.NUTRDESC, NUTR_VAL, UNITS  FROM FOOD_DES fd LEFT JOIN NUT_DATA ndat ON fd.NDB_NO = ndat.NDB_NO  LEFT JOIN NUTR_DEF ndef ON ndat.NUTR_NO = ndef.NUTR_NO order by ndef.SR_ORDER DESC;
